<?php	if(isset($_GET['idbaiviet'])) {	$idbaiviet = addslashes($_GET['idbaiviet']);	$sql="select tenbaiviet,metakeywords,metadescription from baiviet where loaibaiviet=4 AND anhien=1 AND idbaiviet=".$idbaiviet;	$rs=mysql_query($sql);	$row=mysql_fetch_assoc($rs);?><title><?php echo $row['tenbaiviet'] ?></title><meta content="<?php echo $row['metakeywords'] ?>" name="keywords"><meta content="<?php echo $row['metadescription'] ?>" name="description"><?php}else{?><title>News</title><meta content="News" name="keywords"><meta content="News" name="description"><?php}?>